// Helper functions
const timeToKey = (t) => {
  if (!t || t.trim() === '') return null;
  const [h, m] = t.split(':').map(Number);
  return h * 100 + m;
};

const isEarlier = (a, b) => {
  if (!a || !b) return null;
  const [ah, am] = a.split(':').map(Number);
  const [bh, bm] = b.split(':').map(Number);
  return ah * 60 + am < bh * 60 + bm;
};

const isLater = (a, b) => {
  if (!a || !b) return null;
  const [ah, am] = a.split(':').map(Number);
  const [bh, bm] = b.split(':').map(Number);
  return ah * 60 + am > bh * 60 + bm;
};

// Helper for SQL-safe values
const sqlVal = (v) =>
  v === null || v === undefined ? 'NULL'
  : typeof v === 'string' ? `'${v.replace(/'/g, "''")}'`
  : v;

// Build VALUES rows
const values = $input.all().map(item => {
  const j = item.json;

  const apptDateKey = j.appointment_date?.replace(/-/g, '') ?? null;
  const schedDateKey = j.scheduling_date?.replace(/-/g, '') ?? null;

  return `(
    ${sqlVal(j.appointment_id)},
    ${sqlVal(j.slot_id)},
    ${sqlVal(apptDateKey ? parseInt(apptDateKey) : null)},
    ${sqlVal(schedDateKey ? parseInt(schedDateKey) : null)},
    ${sqlVal(timeToKey(j.appointment_time))},
    (SELECT patient_key FROM dim_patient WHERE patient_id = ${sqlVal(j.patient_id)}),
    (SELECT status_key FROM dim_status WHERE status_code = ${sqlVal(j.status)}),
    (SELECT age_group_key FROM dim_age_group WHERE age_group_code = ${sqlVal(j.age_group)}),
    ${sqlVal(j.scheduling_interval ? parseInt(j.scheduling_interval) : null)},
    ${sqlVal(j.appointment_duration ? parseFloat(j.appointment_duration) : null)},
    ${sqlVal(j.waiting_time ? parseFloat(j.waiting_time) : null)},
    ${sqlVal(j.age ? parseInt(j.age) : null)},
    ${j.scheduling_date === j.appointment_date},
    ${sqlVal(isEarlier(j.check_in_time, j.appointment_time))},
    ${sqlVal(isLater(j.check_in_time, j.appointment_time))}
  )`;
}).join(',\n');

// Final SQL
const sql = `
INSERT INTO fact_appointment (
  appointment_id,
  slot_id,
  appointment_date_key,
  scheduling_date_key,
  appointment_time_key,
  patient_key,
  status_key,
  age_group_key,
  scheduling_lead_days,
  appointment_duration_min,
  waiting_time_min,
  age_at_appointment,
  is_same_day_booking,
  arrived_early,
  arrived_late
)
VALUES
${values};
`;

return [
  {
    json: { sql }
  }
];
